import { takeEvery } from "redux-saga/effects";

export function* sagaCallBack() {
    console.log('im inside fetch user');


}

export function* todoSaga() {
    yield takeEvery('FETCH_USERS', () => {
        console.log('im inside fetch user');
    });
}
