import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { TodosReducer } from '../store/todos/todoReducer';

function TodosComponent() {

    const dispatch = useDispatch();
    const { data, loading, error } = useSelector(TodosReducer)



    const loadingUI = () => {
        return (
            <p>Loading...</p>
        )
    }
    const errorUI = () => {
        return (
            <p>Error :(</p>
        )
    }
    const dataUI = () => {
        return (
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">First</th>
                        <th scope="col">Last</th>
                        <th scope="col">Handle</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>

                </tbody>
            </table>
        )
    }
    return (
        <div><button onClick={() => dispatch({ type: 'FETCH_USERS' })}>Get remote API data</button>

            <div>
                {loading && loadingUI()}
                {error && errorUI()}
                {data && dataUI()}

            </div>

        </div>
    )
}

export default TodosComponent
