import React from 'react'
import { useDispatch } from 'react-redux'

function TodoComponent() {

    const dispatch = useDispatch



    const loadingUI = () => {
        return (
            <p>Loading...</p>
        )
    }
    const errorUI = () => {
        return (
            <p>Error :(</p>
        )
    }
    const dataUI = () => {
        return (
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">First</th>
                        <th scope="col">Last</th>
                        <th scope="col">Handle</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>

                </tbody>
            </table>
        )
    }
    return (
        <div><button>Get remote API data</button>

            <div>
                {dataUI()}
            </div>

        </div>
    )
}

export default TodoComponent
